  transaction_id, log_type, create_ip, 
  create_person, create_time, update_person, 
  update_time, request_header, request_params
  )
values (#{id,jdbcType=BIGINT}, #{appId,jdbcType=VARCHAR}, #{appOrderId,jdbcType=VARCHAR}, 
  #{transactionId,jdbcType=VARCHAR}, #{logType,jdbcType=VARCHAR}, #{createIp,jdbcType=VARCHAR}, 
  #{createPerson,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updatePerson,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{requestHeader,jdbcType=LONGVARCHAR}, #{requestParams,jdbcType=LONGVARCHAR}
  )
